INSERT INTO `item_produk_layanan` (`id`, `id_jenis`, `judul`, `cover`, `slug`, `text_top`, `konten`, `created_at`, `updated_at`) VALUES
(9, 2, 'KRIDA MAS', 'upload/produk-layanan/20210728192116KRIDA MAS_2018-11-01_08_33_31.jpg', 'krida-mas', 'Krida Mas Adalah fasilitas kredit yang diberikan kepada masyarakat dengan jaminan emas. Keunggulan kredit ini adalah proses yang cepat, kredit cair dalam waktu 10 menit dan dilengkapi dengan mobil kas keliling.', , '2021-07-28 12:21:16', '2021-07-28 12:21:16'),
(10, 2, 'MULUS', 'upload/produk-layanan/20210728192151MULUS_2018-11-01_08_34_17.jpg', 'mulus', 'MULTI LANCAR USAHA adalah kredit yang diperuntukkan sektor usaha produktif non pertanian. Yang berhak memperoleh Kredit Multi Lancar Usaha (MULUS) adalah Penduduk Jawa Timur yang mempunyai sektor usaha produktif non pertanian.', , '2021-07-28 12:21:51', '2021-07-28 12:21:51'),
(11, 2, 'DAGULIR', 'upload/produk-layanan/20210728192242DAGULIR_2018-11-01_08_34_55.jpg', 'dagulir', 'Kredit DAGULIR Merupakan Program Kredit pada Bank BPR Jatim-Bank UMKM Jawa Timur yang Bersumber Dari APBD Provinsi Jawa Timur yang Diperuntukkan Bagi Usaha Mikro, Kecil, Menengah dan Koperasi di Jawa Timur.', , '2021-07-28 12:22:42', '2021-07-28 12:22:42'),
(12, 2, 'PKPJ', 'upload/produk-layanan/20210728192322PKPJ_2018-11-01_08_37_52.jpg', 'pkpj', 'Paket Kredit Petani Jawa Timur (PKPJ) adalah fasilitas kredit yang diberikan kepada petani perorangan/kelompok tani/koperasi yang mempunyai kegiatan usaha dibidang pertanian, peternakan, perikanan, dan perkebunan.', , '2021-07-28 12:23:22', '2021-07-28 12:23:22'),
(13, 2, 'KUIPS', 'upload/produk-layanan/20210728192403KUIPS_2018-11-01_09_27_11.jpg', 'kuips', 'Kredit Usaha Industri Primer dan/ atau Sekunder (KUIP/S) adalah kredit yang diperuntukkan bagi usaha yang bergerak pada kegiatan pengolahan pertama dan/ atau kegiatan pengolahan tahap kedua dari hasil produksi sektor industri primer (hasil pertanian, perkebunan, perikanan & kelautan, kehutanan, peternakan, dan pertambangan);', , '2021-07-28 12:24:03', '2021-07-28 12:24:03');
INSERT INTO `item_produk_layanan` (`id`, `id_jenis`, `judul`, `cover`, `slug`, `text_top`, `konten`, `created_at`, `updated_at`) VALUES
(14, 2, 'HIPPAMS', 'upload/produk-layanan/20210728192451HIPPAMS_2019-09-02_16_31_53.jpg', 'hippams', 'Hippams adalah Himpunan Penduduk Pemakai Air Minum dan Sanitasi.', , '2021-07-28 12:24:51', '2021-07-28 12:24:51'),
(15, 3, 'ATM', 'upload/produk-layanan/20210728193049gpn.png', 'atm', 'Bank Perkeditan Rakyat Jawa Timur telah memiliki ijin sebagai penerbit Kartu ATM berdasarkan surat Bank Indonesia Nomor: 15/ 24/ DASP tanggal 18 Januari 2013 tentang Permohonan Izin Penerbitan Kartu ATM.', , '2021-07-28 12:30:49', '2021-07-28 12:30:49'),
(16, 3, 'MOBIL KAS', 'upload/produk-layanan/20210728193259bpr-nusamba-1068x491.jpeg', 'mobil-kas', 'Mobil Kas Keliling adalah salah satu fasilitas unggulan dari Bank BPR Jatim, berupa armada mobil kas yang memiliki berbagai macam fasilitas bagi nasabah Bank BPR Jatim, diantaranya:', , '2021-07-28 12:32:59', '2021-07-28 12:32:59'),
(17, 3, 'MONEY CHANGER', 'upload/produk-layanan/20210728193347k71lcdya6enwpfpl42sa.jpg', 'money-changer', 'Fasilitas layanan Bank BPR Jatim Bank UMKM Jawa Timur dalam memberikan pelayanan kepada nasabah untuk melakukan transaksi jual beli mata uang asing.', , '2021-07-28 12:33:47', '2021-07-28 12:33:47'),
(18, 3, 'WAROENG DOEIT', 'upload/produk-layanan/20210728193625Dua-Sisi-Jakarta-860x682.jpg', 'waroeng-doeit', 'Fasilitas layanan Bank BPR Jatim Bank UMKM Jawa Timur berbasis Online dengan fitur antara lain sebagai Payment Point Online Bank (PPOB), Pembayaran tagihan, Pembelian dan Pengiriman uang (Remittance) .', , '2021-07-28 12:36:25', '2021-07-28 12:36:25');

